mapper.insert:
insert into sys_tree

	ID

		,PARENT


		,NAME


		,NODEDESC


		,DISCRIMINATOR


		,TREEID


		,SORT


		,CODE


		,icon


		,iconCls


		,url


		,locked


		,CREATEDATE 


		,CREATEBY 


		,UPDATEBY 


		,UPDATEDATE 




	#{id, jdbcType=BIGINT}


		,#{parentId, jdbcType=BIGINT}


		,#{name, jdbcType=VARCHAR}


		,#{desc, jdbcType=VARCHAR}


		,#{discriminator, jdbcType=VARCHAR}


		,#{treeId, jdbcType=VARCHAR}


		,#{sort, jdbcType=INTEGER}


		,#{code, jdbcType=VARCHAR}


		,#{icon, jdbcType=VARCHAR}


		,#{iconCls, jdbcType=VARCHAR}


		,#{url, jdbcType=VARCHAR}


		,#{locked, jdbcType=INTEGER}


		,#{createDate, jdbcType=TIMESTAMP}


		,#{createBy, jdbcType=VARCHAR}


		,#{updateBy, jdbcType=VARCHAR}


		,#{updateDate, jdbcType=TIMESTAMP}

mapper.select:
select E.* from sys_tree E


	1 = 1


		and E.PARENT = #{parentId}



		and E.PARENT IN

			#{x_parentId}




		and E.DISCRIMINATOR = #{discriminator}



		and E.NAME = #{name}



		and E.NAME like #{nameLike}



		and E.NODEDESC like #{descLike}



		and E.SORT >= #{sortGreaterThanOrEqual}



		and E.SORT > #{sortGreaterThan}



		and E.SORT <= #{sortLessThanOrEqual}



		and E.SORT < #{sortLessThan}



		and E.CODE = #{code}



		and E.CODE like #{codeLike}



		and E.CODE IN

			#{x_code}




		and E.locked = #{locked}



		and E.locked >= #{lockedGreaterThanOrEqual}



		and E.locked <= #{lockedLessThanOrEqual}






		order by ${orderBy}


		order by E.SORT asc

SELECT r.id, r.rolename, r.busiess_id, r.domain_index
FROM net_role r
WHERE r.id = #{roleId}

SELECT x.UserID, x.UserName, x.email, x.Mobile, x.depid, x.issystem, x.domain_index 
FROM UserInfo x
WHERE 1=1 
AND (
   x.UserID LIKE #{searchWord}
   OR 
   x.UserName LIKE #{searchWord}
   OR
   x.email LIKE #{searchWord}
   OR
   x.Mobile LIKE #{searchWord}
)

ORDER BY x.UserID ASC

SELECT x.UserID, x.UserName, x.email, x.Mobile, x.depid, x.Password, x.issystem, x.domain_index
FROM UserInfo x
WHERE 1=1 



		and x.UserID = #{activationCode} 


		and x.UserID = #{actorId} 


		and x.UserID IN

			#{x_actorId}



		AND ( x.depid = #{deptId} ) 


		AND x.depid IN

			#{x_deptId}






ORDER BY x.UserID ASC

select distinct r.CODE from net_role r inner join userrole ur on r.ID = ur.ROLEID inner join UserInfo u on ur.USERID = u.USERID where r.CODE is not null
and u.UserID = #{actorId}
and u.UserID IN
#{x_actorId}
and ( u.depid = #{nodeId} )
and u.depid IN
#{x_nodeId}
and 1=0
union
select E.code from sys_application E inner join sys_access A on E.id = A.appId where E.code is not null and A.roleId in ( select ur.id from userrole ur inner join UserInfo u on ur.USERID = u.USERID where 1=1
and u.UserID = #{actorId}
and u.UserID IN
#{x_actorId}
and ( u.depid = #{nodeId} )
and u.depid IN
#{x_nodeId}
and 1=0
)
union
select E.CODE from sys_function E inner join sys_permission A on E.id = A.funcId where E.CODE is not null and A.roleId in ( select ur.id from userrole ur inner join UserInfo u on ur.USERID = u.USERID where 1=1
and u.UserID = #{actorId}
and u.UserID IN
#{x_actorId}
and ( u.depid = #{nodeId} )
and u.depid IN
#{x_nodeId}
and 1=0
)
