		,LINKPARAM


		,PRINTTYPE


		,PRINTFILEID


		,PRINTFILENAME


		,PRINTPARAM


		,REFID1


		,REFNAME1


		,REFID2


		,REFNAME2


		,REFID3


		,REFNAME3


		,REFID4


		,REFNAME4


		,REFID5


		,REFNAME5


		,CREATEDATE


		,CREATEBY


		,UPDATEBY


