mapper.insert:
insert into UserInfo

	USERID

		,PASSWORD


		,USERNAME


		,STATUS


		,CTIME


		,MOBILE


		,EMAIL


		,TELEPHONE


		,HEADSHIP


		,USERTYPE


		,ACCOUNTTYPE


		,DEPID


		,ISSYSTEM


		,SUPERIORIDS




	#{account, jdbcType=VARCHAR}


		,#{password, jdbcType=VARCHAR}


		,#{name, jdbcType=VARCHAR}


		,#{status, jdbcType=INTEGER}


		,#{createTime, jdbcType=TIMESTAMP}


		,#{mobile, jdbcType=VARCHAR}


		,#{email, jdbcType=VARCHAR}


		,#{telephone, jdbcType=VARCHAR}


		,#{headship, jdbcType=VARCHAR}


		,#{userType, jdbcType=INTEGER}


		,#{accountType, jdbcType=INTEGER}


		,#{deptId, jdbcType=BIGINT}


		,#{adminFlag, jdbcType=VARCHAR}


		,#{superiorIds, jdbcType=VARCHAR}

mapper.update:

update UserInfo
set PASSWORD = #{password, jdbcType=VARCHAR}
where USERID = #{account, jdbcType=VARCHAR}

mapper.delete:
delete from UserInfo
where (
USERID IN

	#{x_rowId}

)

mapper.select:
select count(distinct E.USERID) from UserInfo E
inner join userrole R
on E.USERID = R.USERID
where 1=1


	and E.DEPID = #{deptId}



	and E.USERNAME like #{nameLike}



	and R.AVAILDATESTART >=
	#{availDateStartGreaterThanOrEqual}



	and R.AVAILDATESTART <=
	#{availDateStartLessThanOrEqual}



	and R.AVAILDATEEND >= #{availDateEndGreaterThanOrEqual}



	and R.AVAILDATEEND <= #{availDateEndLessThanOrEqual}

select distinct E.* from UserInfo E
inner join userrole R
on E.USERID =
R.USERID
where 1=1


	and E.DEPID = #{deptId}



	and E.UserName like #{nameLike}



	and R.AVAILDATESTART >=
	#{availDateStartGreaterThanOrEqual}



	and R.AVAILDATESTART <=
	#{availDateStartLessThanOrEqual}



	and R.AVAILDATEEND >= #{availDateEndGreaterThanOrEqual}



	and R.AVAILDATEEND <= #{availDateEndLessThanOrEqual}


order by E.UserID asc

select u.USERID as actorId, u.USERNAME as actorName, r.ID as roleId,
       r.CODE as roleCode, r.ROLENAME as roleName
from USERINFO u
inner join USERROLE ur
on u.USERID = ur.USERID
inner join NET_ROLE r
on ur.ROLEID = r.ID
where 1=1


	and u.USERID = #{actorId}



	and u.USERID IN

		#{x_account}




	and u.DEPID = #{deptId}



	and u.DEPID IN

		#{x_deptId}




	and r.ID = #{roleId}



	and r.ID IN

		#{x_roleId}




	and r.CODE = #{roleCode}



	and r.CODE IN

		#{x_roleCode}

select E.*



		order by ${orderBy}


		order by E.UserID asc

mapper.sql:
from UserInfo E


	inner join sys_department D
	on E.DEPID = D.ID



	1 = 1


	    and D.NAME like #{deptNameLike}



		and E.USERID = #{account}



		and E.USERID like #{accountLike}



		and E.USERID IN

			#{x_account}




		and E.USERNAME = #{name}



		and E.USERNAME like #{nameLike}



		and E.USERNAME IN

			#{x_name}




		and E.CTIME >= #{createTimeGreaterThanOrEqual}



		and E.CTIME <= #{createTimeLessThanOrEqual}



		and E.MOBILE = #{mobile}



		and E.MOBILE like #{mobileLike}



		and E.EMAIL = #{email}



		and E.EMAIL like #{emailLike}



		and E.DEPID = #{deptId}



		and E.DEPID IN

			#{x_deptId}




		and E.ISSYSTEM = #{adminFlag}
