mapper.delete:
delete from PFILE
where (
ID IN

	#{x_id}

)

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.ID desc

mapper.sql:
from PFILE E



		inner join volume v
		on E.vid = v.vid
		inner join
		treevmain_u u
		on v.topid = u.index_id


		inner join volume v
		on E.vid = v.vid
		inner join
		treevmain_u u
		on v.topid = u.index_id




	1 = 1


		${dms_treenodes}



		and u.id like #{treenodeLike}



		and E.EFILENUM = #{efilenum}



		and E.LISTNUM = #{listnum}



		and E.LISTNUM like #{listnumLike}



		and E.LIST_ID = #{listId}



		and E.LIST_ID >= #{listIdGreaterThanOrEqual}



		and E.LIST_ID <= #{listIdLessThanOrEqual}



		and E.LIST_ID IN

			#{x_listId}





		and E.INDEX_ID = #{indexId}



		and E.INDEX_ID >= #{indexIdGreaterThanOrEqual}



		and E.INDEX_ID <= #{indexIdLessThanOrEqual}



		and E.INDEX_ID IN

			#{x_indexId}





		and E.PID = #{pid}



		and E.PID >= #{pidGreaterThanOrEqual}



		and E.PID <= #{pidLessThanOrEqual}



		and E.PID IN

			#{x_pid}





