mapper.insert:
insert into flow_activ 

    id

		,activ_d_id 


		,process_id 


		,typeofact 


		,name 


		,content 


		,strfuntion 


		,netroleid 


		,userid 


		,listno 


		,timelimit 


		,ctime_start 


		,ctime_end 


		,state 


		,intback 




	  #{id, jdbcType=VARCHAR}


		,#{activDefId, jdbcType=VARCHAR}


		,#{processId, jdbcType=VARCHAR}


		,#{typeofact, jdbcType=VARCHAR}


		,#{name, jdbcType=VARCHAR}


		,#{content, jdbcType=VARCHAR}


		,#{strfuntion, jdbcType=VARCHAR}


		,#{netroleid, jdbcType=VARCHAR}


		,#{userid, jdbcType=VARCHAR}


		,#{listno, jdbcType=INTEGER}


		,#{timelimit, jdbcType=DOUBLE}


		,#{ctimeStart, jdbcType=TIMESTAMP}


		,#{ctimeEnd, jdbcType=TIMESTAMP}


		,#{state, jdbcType=INTEGER}


		,#{intback, jdbcType=INTEGER}

mapper.select:
select * from flow_activ where process_id in(
  select id from flow_process where main_id in(
     select id from cell_mytaskmain where index_id=#{mytaskmainIndexId} and mycell_tasks_id=#{mycellTaskId} and intisflow=2
  )
) 
and activ_d_id=#{activityDefId}

mapper.sql:
		from flow_activ E



		       1 = 1  


				and E.activ_d_id = #{activDefId}



				and E.activ_d_id like #{activDefIdLike}



			    and E.activ_d_id IN

                  #{x_activDefId}





				and E.process_id = #{processId}



				and E.process_id like #{processIdLike}



			    and E.process_id IN

                  #{x_processId}





				and E.typeofact = #{typeofact}



				and E.typeofact like #{typeofactLike}



			    and E.typeofact IN

                  #{x_typeofact}




				and E.name like #{nameLike}



				and E.content like #{contentLike}



				and E.strfuntion = #{strfuntion}



				and E.netroleid = #{netroleid}



				and E.netroleid like #{netroleidLike}



			    and E.netroleid IN

                  #{x_netroleid}




				and E.userid = #{userid}



				and E.userid like #{useridLike}



			    and E.userid IN

                  #{x_userid}




				and E.timelimit >= #{timelimitGreaterThanOrEqual}



				and E.timelimit <= #{timelimitLessThanOrEqual}



				and E.ctime_start >= #{ctimeStartGreaterThanOrEqual}



				and E.ctime_start <= #{ctimeStartLessThanOrEqual}



				and E.ctime_end >= #{ctimeEndGreaterThanOrEqual}



				and E.ctime_end <= #{ctimeEndLessThanOrEqual}



				and E.state = #{state}



				and E.state >= #{stateGreaterThanOrEqual}



				and E.state <= #{stateLessThanOrEqual}



			    and E.state IN

                  #{x_state}




				and E.intback = #{intback}



				and E.intback >= #{intbackGreaterThanOrEqual}



				and E.intback <= #{intbackLessThanOrEqual}



			    and E.intback IN

                  #{x_intback}
