mapper.select:
select * from user_task 
where actorId = #{actorId}
and index_name = 'activ'

SELECT id
FROM cell_mytaskmain
WHERE (id IN
(SELECT Main_id
FROM flow_process
WHERE EXISTS
(SELECT *
FROM flow_activ
WHERE (userid = #{actorId}

	OR (userid IS NULL AND netroleid IN

		#{x_roleId}

	)

)

	AND state = #{state}


	AND intback = #{intback}

AND process_id = flow_process.id)))
ORDER BY id DESC

		SELECT DISTINCT index_id
		FROM cell_mytaskmain
		WHERE (id IN

				#{x_taskId}

		)
        ORDER BY index_id DESC

		SELECT ctime
		FROM cell_mytaskmain
		WHERE (id IN

				#{x_taskId}

		)
        ORDER BY ctime DESC

SELECT index_name, index_id, parent_id FROM treepinfo WHERE ( projtype IN ('1', '3')) AND ( (( SELECT TOP 1 id FROM treepinfo WHERE index_id = -1 ) LIKE id + '%' )
OR (( SELECT TOP 1 id FROM treepinfo WHERE index_id = #{x_indexId} ) LIKE id + '%' )
) ORDER BY index_id

SELECT c.id as activityId, a.id as taskMainId, a.ctime as createTime, a.index_id as indexId, a.task_id as taskId, a.name as subject, a.projname as projectName, a.listno as listno, a.type_index_id as typeIndexId, a.flagint as flagint, a.mycell_tasks_id as mycellTasksId, a.fromtasksid as fromTasksId, a.totaskid as toTaskId, a.intfinish as intfinish, a.type_tablename as typeTablename, a.type_id as typeId, a.userid as actorId, a.netroleid as netRoleId, a.intisflow as intisflow, a.intStop as intStop, a.filetype_index filetypeIndex, c.state as state,c.ctime_start as startTime, c.ctime_end as endTime, c.timelimit as timelimit FROM cell_mytaskmain a INNER JOIN flow_process b ON a.id = b.main_id INNER JOIN flow_activ c ON b.id = c.process_id WHERE (a.id IN
#{x_taskId}
) AND (c.id IN (SELECT id FROM flow_activ WHERE (userid = #{actorId}
OR (userid IS NULL AND netroleid IN
#{x_roleId}
)
)
AND state = #{state}
AND intback = #{intback}
)) AND (a.index_id IN (SELECT index_id FROM treepinfo WHERE id LIKE (SELECT TOP 1 id FROM treepinfo WHERE index_id = #{indexId} ) + '%'))

	SELECT c.id as activityId, a.id as taskMainId, a.ctime as createTime, a.index_id as indexId, 
	a.task_id as taskId, a.name as subject, a.projname as projectName, a.listno as listno, 
    a.type_index_id as typeIndexId, a.flagint as flagint, a.mycell_tasks_id as mycellTasksId,
	a.fromtasksid as fromTasksId, a.totaskid as toTaskId, a.intfinish as intfinish, 
	a.type_tablename as typeTablename, a.type_id as typeId, a.userid as actorId, 
	a.netroleid as netRoleId, a.intisflow as intisflow, a.intStop as intStop, 
    a.filetype_index filetypeIndex, c.state as state,c.ctime_start as startTime,
	c.ctime_end as endTime, c.timelimit as timelimit
	FROM cell_mytaskmain a INNER JOIN
		 flow_process b ON a.id = b.main_id INNER JOIN
		 flow_activ c ON b.id = c.process_id
	WHERE (a.id IN 

				#{x_taskId}

	) AND  (c.id IN
			 (SELECT id
		   FROM flow_activ
		   WHERE (userid = #{actorId} 

						OR (userid IS NULL AND netroleid IN

							#{x_roleId}

						)

				)


		AND state = #{state}


		AND intback = #{intback}

		 )
	   )  



		AND a.ctime >= #{ctimeGreaterThanOrEqual}


		AND a.ctime <= #{ctimeLessThanOrEqual}


select E.*





	order by ${orderBy}


	 order by E.startTime desc


select E.*





	  order by ${orderBy}


	  order by E.startTime desc

mapper.sql:
from user_task E



	1 = 1


		and E.parent_id = #{parentId}



		and E.parent_id IN

			#{x_parentId}




		and E.parent_id != #{parentIdNotEqual}



		and E.actorId = #{actorId}



		and E.actorId IN

			#{x_actorId}




		and E.name = #{name}



		and E.name like #{nameLike}



		and E.index_name like #{indexNameLike}



		and E.activityId = #{activityId}



		and E.activityId IN

			#{x_activityId}




		and E.startTime >= #{startTimeGreaterThanOrEqual}



		and E.startTime <= #{startTimeLessThanOrEqual}



		and E.endTime >= #{endTimeGreaterThanOrEqual}



		and E.endTime <= #{endTimeLessThanOrEqual}



		and E.state = #{state}



		and E.state >= #{stateGreaterThanOrEqual}



		and E.state <= #{stateLessThanOrEqual}



		and E.state IN

			#{x_state}




		and E.timelimit >= #{timelimitGreaterThanOrEqual}



		and E.timelimit <= #{timelimitLessThanOrEqual}



		and E.lastModified >=
		#{lastModifiedGreaterThanOrEqual}



		and E.lastModified <= #{lastModifiedLessThanOrEqual}
